"use client";

import { useState } from "react";
import { Preview } from "./preview-generation"; 
import { ErrorGeneration } from "./error-generation"; 
import { FormGeneration } from "./form-generation"; 
import { HeaderGeneration } from "./header-generation"; 

interface CardSettings {
    style: string;
    backgroundColor: string;
    lighting: string;
    pose: string;
    aspectRatio: string;
}

export default function AICardGeneration() {
    const [showForm, setShowForm] = useState(true);
    const [error, setError] = useState<string | null>(null);
    const [isLoading, setIsLoading] = useState(false);
    const [settings, setSettings] = useState<CardSettings>({
        style: "artistic",
        backgroundColor: "studio",
        lighting: "studio",
        pose: "profile",
        aspectRatio: "4:5",
    });

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setShowForm(false);
        setIsLoading(true);
        setError(null);

        try {
            await new Promise((resolve) => setTimeout(resolve, 3000));
            setShowForm(false);
        } catch (err) {
            setError("Failed to generate video. Please try again.");
        } finally {
            setIsLoading(false);
        }
    };

    const handleBackToSettings = () => {
        setShowForm(true);
        setError(null);
    };

    return (
        <div className="group relative overflow-hidden w-full max-w-sm bg-white dark:bg-zinc-900 border border-zinc-200 dark:border-zinc-800 rounded-2xl transition-all duration-300 hover:shadow-[0_8px_30px_rgb(0,0,0,0.12)] dark:hover:shadow-[0_8px_30px_rgb(0,0,0,0.2)] min-h-[500px] flex flex-col justify-between gap-2">
            <HeaderGeneration />
            <div className="flex-1 overflow-hidden flex flex-col">
                {error && <ErrorGeneration error={error} />}

                {showForm ? (
                    <FormGeneration
                        onSubmit={handleSubmit}
                        settings={settings}
                        onSettingsChange={setSettings}
                    />
                ) : (
                    <div className="p-4">
                        <Preview
                            isLoading={isLoading}
                            imageUrl="https://ferf1mheo22r9ira.public.blob.vercel-storage.com/profile-mjss82WnWBRO86MHHGxvJ2TVZuyrDv.jpeg"
                        />

                        {!isLoading && (
                            <div className="space-y-4">
                                <div className="p-3 space-y-2 bg-zinc-50 dark:bg-zinc-800/50 rounded-xl">
                                    <div className="flex justify-between text-sm">
                                        <span className="text-zinc-500">
                                            Quality
                                        </span>
                                        <span className="text-zinc-900 dark:text-zinc-100">
                                            1080p
                                        </span>
                                    </div>
                                    <div className="flex justify-between text-sm">
                                        <span className="text-zinc-500">
                                            Duration
                                        </span>
                                        <span className="text-zinc-900 dark:text-zinc-100">
                                            00:07
                                        </span>
                                    </div>
                                </div>

                                <div className="flex items-center justify-between gap-2">
                                    <button
                                        type="button"
                                        onClick={handleBackToSettings}
                                        className="w-full h-9 flex items-center justify-center gap-2 border border-zinc-200 dark:border-zinc-700 text-zinc-900 dark:text-zinc-100 text-sm font-medium rounded-xl hover:bg-zinc-50 dark:hover:bg-zinc-800 transition-colors"
                                    >
                                        Back to Settings
                                    </button>
                                    <button
                                        type="button"
                                        className="w-full h-9 flex items-center justify-center gap-2 bg-zinc-900 hover:bg-zinc-800 dark:bg-white dark:hover:bg-zinc-100 dark:text-zinc-900 text-white text-sm font-medium rounded-xl transition-colors"
                                    >
                                        Download
                                    </button>
                                </div>
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
}

import React from "react";

export const HeaderGeneration = () => {
    return (
        <div className="p-4 flex items-center justify-between border-b border-zinc-200 dark:border-zinc-800">
            <div className="flex items-center gap-3">
                <div>
                    <h3 className="text-sm font-medium text-zinc-900 dark:text-zinc-100">
                        AI Portrait Generation
                    </h3>
                    <p className="text-xs text-zinc-500 dark:text-zinc-400">
                        Create stunning portraits with AI
                    </p>
                </div>
            </div>
        </div>
    );
};

import { MessageCircle, Sparkles } from "lucide-react";
import { SettingsGeneration } from "./settings-generation";
import { Input } from "@/components/ui/input";

interface VideoSettings {
    style: string;
    backgroundColor: string;
    lighting: string;
    pose: string;
    aspectRatio: string;
}

interface FormProps {
    onSubmit: (e: React.FormEvent) => void;
    settings: VideoSettings;
    onSettingsChange: (settings: VideoSettings) => void;
}

export const FormGeneration = ({
    onSubmit,
    settings,
    onSettingsChange,
}: FormProps) => {
    return (
        <form
            onSubmit={onSubmit}
            className="flex flex-col gap-2 flex-1 p-4 justify-between"
        >
            <div className="space-y-2">
                <div className="flex items-center gap-2">
                    <MessageCircle className="w-4 h-4 text-zinc-500" />
                    <span className="text-sm text-zinc-500">Prompt</span>
                </div>
                <Input
                    type="text"
                    size={16}
                    defaultValue="Picture of a women with orange background"
                    placeholder="Describe the portrait you want to create..."
                    className="w-full bg-zinc-100 dark:bg-zinc-800 text-sm text-zinc-900 dark:text-zinc-100 placeholder:text-zinc-500 rounded-xl focus:outline-hidden focus-visible:ring-offset-0 focus-visible:ring-0 focus-visible:border-zinc-900 dark:focus-visible:border-zinc-100"
                />
            </div>

            <SettingsGeneration
                settings={settings}
                onSettingsChange={onSettingsChange}
            />

            <button
                type="submit"
                className="w-full h-9 flex items-center justify-center gap-2 bg-zinc-900 hover:bg-zinc-800 dark:bg-white dark:hover:bg-zinc-100 dark:text-zinc-900 text-white text-sm font-medium rounded-xl transition-colors self-end"
            >
                <Sparkles className="w-4 h-4" />
                Generate Portrait
            </button>
        </form>
    );
};

"use client";

import React, { useState, useEffect } from "react";
import { Loader2 } from "lucide-react";
import Image from "next/image";
import { Card, CardContent } from "@/components/ui/card";

interface PreviewProps {
    isLoading: boolean;
    imageUrl: string;
}

export const Preview = ({ isLoading, imageUrl }: PreviewProps) => {
    const availableTexts = [
        "Creating your masterpiece...",
        "Finding the good colors...",
        "Adding the final touches...",
    ];
    const [currentTextIndex, setCurrentTextIndex] = useState(0);
    const [progress, setProgress] = useState(0);

    useEffect(() => {
        if (!isLoading) {
            setProgress(0);
            return;
        }

        const interval = setInterval(() => {
            setProgress((prev) => {
                if (prev >= 100) {
                    clearInterval(interval);
                    return 100;
                }
                return prev + 1;
            });
        }, 30);

        return () => clearInterval(interval);
    }, [isLoading]);

    useEffect(() => {
        if (!isLoading) return;

        const interval = setInterval(() => {
            setCurrentTextIndex((prev) => (prev + 1) % availableTexts.length);
        }, 1000);

        return () => clearInterval(interval);
    }, [isLoading]);

    return (
        <div className="aspect-video  rounded-xl mb-4 flex items-center justify-center">
            {isLoading ? (
                <Card className="w-full max-w-md border-0 shadow-none bg-transparent">
                    <CardContent className="flex flex-col items-center gap-4 p-6">
                        <div className="relative w-12 h-12">
                            <Loader2 className="w-full h-full animate-spin text-fuchsia-500" />
                            <div className="absolute inset-0 bg-linear-to-tr from-transparent via-transparent to-fuchsia-500/10 rounded-full animate-spin-slow" />
                        </div>
                        <div className="space-y-1 text-center">
                            <p className="text-sm font-medium text-zinc-700 dark:text-zinc-300">
                                {availableTexts[currentTextIndex]}
                            </p>
                            <p className="text-xs text-zinc-500 dark:text-zinc-400">
                                This usually takes 10-15 seconds
                            </p>
                        </div>
                        <div className="w-full h-1.5 bg-zinc-200 dark:bg-zinc-700 rounded-full overflow-hidden">
                            <div
                                className="h-full bg-fuchsia-500 transition-all duration-300 ease-linear"
                                style={{ width: `${progress}%` }}
                            />
                        </div>
                    </CardContent>
                </Card>
            ) : (
                <div className="flex flex-col items-center gap-2">
                    <Image
                        src={imageUrl}
                        width={400}
                        height={400}
                        alt="Portrait"
                        className="rounded-xl"
                    />
                </div>
            )}
        </div>
    );
};

import React from "react";
import { AlertCircle } from "lucide-react";

interface ErrorProps {
    error: string;
}

export const ErrorGeneration = ({ error }: ErrorProps) => {
    return (
        <div className="m-4 px-4 py-3 flex items-center gap-2 text-sm text-red-600 dark:text-red-400 bg-red-50 dark:bg-red-900/10 rounded-xl">
            <AlertCircle className="w-4 h-4 shrink-0" />
            <p>{error}</p>
        </div>
    );
};
